<html><head>	<title></title><script language=javascript>function Relleno(){var datos=new Array();var a;datos=document.compras_modulo.articulo.value.split("Ç");document.compras_modulo.ID.value=datos[0];document.compras_modulo.articulo1.value=datos[1];document.compras_modulo.ref.value=datos[2];document.compras_modulo.Pre_COMPRA.value=datos[3];document.compras_modulo.Pre_venta.value=datos[4];document.compras_modulo.CANTIDAD2.value=datos[7];document.compras_modulo.Cant_final.value=datos[9];document.compras_modulo.V_UNIT.value= document.compras_modulo.Pre_venta.value;a=datos[9];parseInt(a); if(a<5) { window.alert("La cantidad de existencias de este articulo esta llegando a un nivel mínimo"); }}function Relleno2(id){	var datos=new Array();	datos=document.compras_modulo.proveedores.value.split("Ç");	document.compras_modulo.ID_proveedor.value=datos[0];	document.compras_modulo.NOMBRE1.value=datos[1];	document.compras_modulo.NOMBRE2.value=datos[2];	document.compras_modulo.APELLIDO1.value=datos[3];	document.compras_modulo.APELLIDO2.value=datos[4];	document.compras_modulo.direccion.value=datos[5];}function multiplica(){if (document.compras_modulo.CANTIDAD.value=="" || document.compras_modulo.CANTIDAD.value==0){	if (document.compras_modulo.ID_proveedor.value==""){		window.alert("Ingrese un número de identificación para el proveedor, para poder completar el proceso con éxito");		document.compras_modulo.ID_proveedor.select();		document.compras_modulo.ID_proveedor.focus();	}	}else{	var a=parseInt(document.compras_modulo.V_UNIT.value);	var b=parseInt(document.compras_modulo.CANTIDAD.value);	var P_VENTA=parseInt(document.compras_modulo.Pre_venta.value);	var P_COMPRA=parseInt(document.compras_modulo.Pre_COMPRA.value);	var cant2=parseInt(document.compras_modulo.CANTIDAD2.value);	var c;	var c_final=parseInt(document.compras_modulo.Cant_final.value);	if(c_final<b){	 window.alert("La cantidad a comprar no puede superar la existente");	 document.compras_modulo.CANTIDAD.value=0;	}	else{	 c=(a*b);	 document.compras_modulo.V_TOTAL.value=parseInt(c);	 document.compras_modulo.Cant_final.value=(c_final-b)	 document.compras_modulo.CANTIDAD2.value =(cant2+b);	 document.compras_modulo.GANANCIA.value =(P_VENTA-P_COMPRA)*b;	}}}function operacion2(){if (document.compras_modulo.cuotas.value=="" || document.compras_modulo.cuotas.value==0){	if (document.compras_modulo.ID_proveedor.value==""){		window.alert("Ingrese un número de identificación para el proveedor, para poder completar el proceso con éxito");		document.compras_modulo.ID_proveedor.select();		document.compras_modulo.ID_proveedor.focus();	}}else{	var a=parseInt(document.compras_modulo.cuotas.value);	var b=parseInt(document.compras_modulo.V_TOTAL.value);	var Valor_cuotas;		Valor_cuotas=(b/a);	 document.compras_modulo.cuotasF.value=parseInt(document.compras_modulo.cuotas.value);	 document.compras_modulo.v_cuotas.value =parseInt(Valor_cuotas);	 document.compras_modulo.Saldo.value=parseInt(document.compras_modulo.V_TOTAL.value);	}}function salir(){self.location.href = "http://localhost/cuentas/BLANK.HTML";}function Cambiar_precio(){if (document.compras_modulo.Pre_venta.value=="" || document.compras_modulo.Pre_venta.value==0){	window.alert("El Precio de venta de este articulo no puede ser cero o una cadena vacia");	document.compras_modulo.Pre_venta.select();	document.compras_modulo.Pre_venta.focus();		exit();}else{	var P_VENTA=parseInt(document.compras_modulo.Pre_venta.value);	var P_COMPRA=parseInt(document.compras_modulo.Pre_COMPRA.value);	if(P_VENTA<P_COMPRA){	 window.alert("El Precio de venta de este articulo esta por debajo del precio en que se compró, de seguir asi generaria un valor negativo en las ganancias");		document.compras_modulo.V_UNIT.value=parseInt(document.compras_modulo.Pre_venta.value);	}else{	document.compras_modulo.V_UNIT.value=parseInt(document.compras_modulo.Pre_venta.value);	}}}</script><script LANGUAGE="JavaScript"><!--if (top == self) self.location.href = "http://localhost/cuentas/";// --></script><STYLE><!--Body {	SCROLLBAR-FACE-COLOR: #0099DD;	SCROLLBAR-HIGHLIGHT-COLOR: #00CCFF;	SCROLLBAR-DARKSHADOW-COLOR: #0000FF;	SCROLLBAR-SHADOW-COLOR: #8000CC;	SCROLLBAR-3DLIGHT-COLOR: #00CCFF;	SCROLLBAR-ARROW-COLOR: #0000FF;	SCROLLBAR-TRACK-COLOR: #99FF00;}--></STYLE><style type="text/css"><!--.color {	background-color: #FF0000;	margin: 2px;	height: 20px;	width: 100px;	font-family: Arial, Helvetica, sans-serif;	font-size: 10px;	font-weight: bold;}--></style><SCRIPT LANGUAGE="JavaScript"><!-- Hiding from other browsers// Browser Detector (v. 1.0)// Author: Jason Tschopp// Email: tschopp@greenapple.com// Url: http://www.greenapple.com/~tschopp/// Notes: Permission is granted to anyone for use // on the internet, provided this header stays with // the script and this header stays in intact.// --></SCRIPT></head><? include('estiloscss/estilo1.css'); ?><body oncontextmenu="return false"   bottommargin="0" leftmargin="0" marginheiht="0" marginwidth="0" rightmargin="0" topmargin="0" bgcolor=#0066CC><table width="100%" cellpadding="0" cellspacing="0" border="2" height="10" ><TR bgColor=#000000>	    <TD HEIGHT="10" align=middle width=87%>			<font color="#FFFFFF">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;			&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;			&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;			&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;¬¬¬¬¬¬¬¬¬¬¬|||  MODULO DE COMPRAS A PROVEEDORES  |||¬¬¬¬¬¬¬¬¬¬¬			</FONT>		</TD>        <TD HEIGHT="10" align=middle bgcolor="#99FF00" >Control Contable ®</TD> </TR></table><FORM METHOD ='POST' Name='compras_modulo' ACTION='CREDITOS.PHP'><table width="100%" cellpadding="5" cellspacing="0" border="0" height="100%" ><center>	<tr valign="top">		<td width="100%">			<table width="100%" cellpadding="8" cellspacing="0" border="0" >				<tr valign="t